const WeatherIcon = ({ weatherCode }: { weatherCode: string }) => {
  let iconComponent
  switch (weatherCode) {
    case '01d':
      iconComponent = <Sunny />
      break
    case '02d':
      iconComponent = <CloudySunny />
      break
    case '03d':
    case '03n':
    case '04d':
    case '04n':
      iconComponent = <Cloudy />
      break
    case '09d':
    case '09n':
      iconComponent = <Shower />
      break
    case '10d':
    case '10n':
      iconComponent = <Rainy />
      break
    case '11d':
    case '11n':
      iconComponent = <Bolt />
      break
    case '13d':
    case '13n':
      iconComponent = <Snow />
      break
    case '50d':
    case '50n':
      iconComponent = <Fog />
      break
    default:
      iconComponent = <Sunny />
      break
  }

  return iconComponent
}

export default WeatherIcon
